//             err: 1,
//             msg: 'Missing inputs !'
//         })
//         const response = await authService.registerService(req.body)
//         return res.status(200).json(response)

//     } catch (error) {
//         return res.status(500).json({
//             err: -1,
//             msg: 'Fail at auth controller: ' + error
//         })
//     }
