            ...queries,
            include: [
                {
                    model: db.Post,
                    as: 'reportPost',
                    attributes: ['id', 'title'],
                    include: [
                        { model: db.User, as: 'user', attributes: ['id', 'name'] }
                    ]
                },
            ]
        })
        resolve({
            err: response ? 0 : 1,
            data: response ? response : 'Something went wrong',
        })
    } catch (error) {
        reject(error)
    }
})
